mapper.update:
 update ${tableName} a
left join   ${tableName} b on a.${code}  = b.${model}
set a.UpdateFlag = '1'
where ${keyword}
and  b.${code} in (${codes})

mapper.select:
select distinct ${column_name}
from
${tableName}
where
${column_name} is not null
${keyword}
and ${column_name} != ''
order by ${column_name}
LIMIT 0,100

select ${url_key} as url_key ,${model_url_key} as model_url_key ,${category_url_key} as category_url_key ,${p_code} as p_code ,${p_name} as p_name ,${p_color} as p_color ,${p_msrp} as p_msrp ,${p_made_in_country} as p_made_in_country ,${pe_short_description} as pe_short_description
,${p_product_type} as p_product_type
,${pe_long_description} as pe_long_description ,${ps_price} as ps_price ,${cps_cn_price} as cps_cn_price ,${cps_cn_price_rmb} as cps_cn_price_rmb ,${cps_cn_price_final_rmb} as cps_cn_price_final_rmb from ${tableName} ${keyword}

select
${image} as image
from
${tableName}
${keyword}
order by ${image}
limit 1

select cfg_val1
from voyageone_cms.cms_mt_feed_config
where is_attribute  = ${isAttribute}
and attribute_type = ${attributeType}
and order_channel_id = ${channelId}

select a.${code}
from ${tableName} a
left join ${tableNameFull} b ON a.${code}  = b.${code}
where b.${code} is null
 or   (b.${code} is not null
  and (a.${category} != b.${category}
   or  a.${model} != b.${model}))

select a.${code}
from ${tableName} a
left join ${tableNameFull} b ON a.${code}  = b.${code}
where b.${code} is not null

select count(*)
from   voyageone_cms.cms_mt_feed_attribute
where channel_id = #{channelId}
and attribute_name = #{attributeName}
and attribute_value = #{attributeValue}

mapper.update:
update ${tableName}
set UpdateFlag = '0'
where ${keyword}
and ${category} in (${categoryvalue})
