mapper.select:
SELECT
    count(1)
FROM
    tt_reservation
WHERE
    source_order_id = #{source_order_id}
AND order_number = #{order_number}
AND order_channel_id = #{order_channel_id}

SELECT
count(1)
FROM
tt_client_tracking
WHERE
source_order_id = #{source_order_id}
AND client_order_id = #{order_number}
AND order_channel_id = #{order_channel_id}
