mapper.select:
select 

from 
	cms_mt_type
where

	type_id = #{typeId,jdbcType=INTEGER}
  	and channel_id = #{channelId,jdbcType=VARCHAR}

    LIMIT #{offset}, #{pageCount}

select 

from 
	cms_mt_value
where 
	type_id = #{typeId,jdbcType=INTEGER}
  	and channel_id = #{channelId,jdbcType=VARCHAR}

    LIMIT #{offset}, #{pageCount}

select 

from 
	cms_mt_attribute
where 
	attribute_id = #{attributeId,jdbcType=INTEGER}
  	and channel_id = #{channelId,jdbcType=VARCHAR}

select 

from 
	cms_mt_attribute_value
where 
	attribute_id = #{attributeId,jdbcType=INTEGER}
  	and attribute_value_id = #{attributeValueId,jdbcType=VARCHAR}
  	and channel_id = #{channelId,jdbcType=VARCHAR}
