mapper.select:
SELECT seq as seq, channel_id as channelId, group_id as groupId, publish_status as publishStatus, creater, created, modifier, modified FROM voyageone_cms2.cms_bt_sx_workload WHERE publish_status=0
AND channel_id=#{channel_id}
order by modified desc limit 0,#{record_count}

mapper.update:
UPDATE
    voyageone_cms2.cms_bt_sx_workload
SET
    publish_status=#{publish_status}
WHERE
    seq=#{seq}
