mapper.select:
select cartId, catId, fieldId, type, created, creater, modified, modifier from voyageone_cms2.cms_mt_platform_special_field
cartId = #{cartId,jdbcType=INTEGER}
AND catId = #{catId,jdbcType=VARCHAR}
AND fieldId = #{fieldId,jdbcType=VARCHAR}
AND type = #{type,jdbcType=VARCHAR}

mapper.delete:
delete from voyageone_cms2.cms_mt_platform_special_field
where
  cartId = #{cartId,jdbcType=INTEGER}
  and catId = #{catId,jdbcType=VARCHAR}
  and fieldId = #{fieldId,jdbcType=VARCHAR}
  and type = #{type,jdbcType=VARCHAR}
