mapper.select:
SELECT * FROM Synship.tm_order_channel
where 1=1

    and order_channel_id = #{order_channel_id}


    and name = #{name}


    and is_usjoi = #{is_usjoi}


    and active = #{active}

ORDER BY order_channel_id

select
cart_id,
name,
short_name,
description,
platform_id,
cart_type,
active,
created
from Synship.ct_cart
where 1=1

    and cart_id = #{cart_id}


    and name like '%${name}%'


    and short_name = #{short_name}


    and description = #{description}


    and platform_id = #{platform_id}


    and cart_type = #{cart_type}


    and active = #{active}

order by created desc

mapper.insert:
UPDATE Synship.ct_cart set name = #{name}, short_name = #{short_name}, description = #{description},
platform_id = #{platform_id}, cart_type = #{cart_type}, active = #{active}, modified = now(), modifier = #{modifier}
WHERE cart_id = #{cart_id}

mapper.update:
update Synship.tm_order_channel
company_id=#{company_id}, company_id=#{company_id}, full_name=#{full_name}, send_name=#{send_name}, send_address=#{send_address}, send_tel=#{send_tel}, send_zip=#{send_zip}, screct_key=#{screct_key}, session_key=#{session_key}, img_url=#{img_url}, is_usjoi=#{is_usjoi}, cart_ids=#{cart_ids}, modifier=#{modifier}, active=#{active}, modified=now()
where order_channel_id=#{order_channel_id}
