mapper.select:
select
`id`,`channel_id`,`cat_id`,`cart_id`,`group_id`,`group_name`,`product_id`,`product_name`,`promotion_id`,`promotion_name`,`model`,`code`,`sku`,`error_type_id`,`error_code`,`error_msg`,`status`,`created`,`creater`,`modified`,`modifier`
from cms_bt_business_log


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `cat_id` = #{catId}


        AND `cart_id` = #{cartId}


        AND `group_id` = #{groupId}


        AND `group_name` = #{groupName}


        AND `product_id` = #{productId}


        AND `product_name` = #{productName}


        AND `promotion_id` = #{promotionId}


        AND `promotion_name` = #{promotionName}


        AND `model` = #{model}


        AND `code` = #{code}


        AND `sku` = #{sku}


        AND `error_type_id` = #{errorTypeId}


        AND `error_code` = #{errorCode}


        AND `error_msg` = #{errorMsg}


        AND `status` = #{status}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`channel_id`,`cat_id`,`cart_id`,`group_id`,`group_name`,`product_id`,`product_name`,`promotion_id`,`promotion_name`,`model`,`code`,`sku`,`error_type_id`,`error_code`,`error_msg`,`status`,`created`,`creater`,`modified`,`modifier`
from cms_bt_business_log



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `cat_id` = #{catId}



        AND `cart_id` = #{cartId}



        AND `group_id` = #{groupId}



        AND `group_name` = #{groupName}



        AND `product_id` = #{productId}



        AND `product_name` = #{productName}



        AND `promotion_id` = #{promotionId}



        AND `promotion_name` = #{promotionName}



        AND `model` = #{model}



        AND `code` = #{code}



        AND `sku` = #{sku}



        AND `error_type_id` = #{errorTypeId}



        AND `error_code` = #{errorCode}



        AND `error_msg` = #{errorMsg}



        AND `status` = #{status}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
