mapper.select:
SELECT
a.id
FROM voyageone_cms2.cms_bt_feed_custom_prop a
WHERE a.channel_id = #{channelId}
AND a.feed_cat_path = #{cat_path}
AND a.feed_prop_original = #{prop_original}

mapper.update:
UPDATE voyageone_cms2.cms_bt_feed_custom_prop

    feed_prop_translation = #{prop_translation},
    modified = now(),
    modifier = #{userName}


    id = #{prop_id}

mapper.select:
SELECT
a.id
FROM voyageone_cms2.cms_bt_feed_custom_prop_value a
WHERE a.channel_id = #{channelId}
AND a.prop_id = #{propId}
AND a.feed_value_original = #{origValue}
