mapper.select:
select
`id`,`channel_id`,`code`,`image_type_id`,`image_url`,`image_name`,`sent_flag`,`created`,`creater`,`modified`,`modifier`
from cms_bt_feed_product_image


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `code` = #{code}


        AND `image_type_id` = #{imageTypeId}


        AND `image_url` = #{imageUrl}


        AND `image_name` = #{imageName}


        AND `sent_flag` = #{sentFlag}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`channel_id`,`code`,`image_type_id`,`image_url`,`image_name`,`sent_flag`,`created`,`creater`,`modified`,`modifier`
from cms_bt_feed_product_image



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `code` = #{code}



        AND `image_type_id` = #{imageTypeId}



        AND `image_url` = #{imageUrl}



        AND `image_name` = #{imageName}



        AND `sent_flag` = #{sentFlag}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
