mapper.select:
select
`id`,`promotion_id`,`org_channel_id`,`model_id`,`product_model`,`cat_path`,`num_iid`,`syn_flg`,`created`,`creater`,`modified`,`modifier`
from cms_bt_promotion_groups


        AND `id` = #{id}


        AND `promotion_id` = #{promotionId}


        AND `org_channel_id` = #{orgChannelId}


        AND `model_id` = #{modelId}


        AND `product_model` = #{productModel}


        AND `cat_path` = #{catPath}


        AND `num_iid` = #{numIid}


        AND `syn_flg` = #{synFlg}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`promotion_id`,`org_channel_id`,`model_id`,`product_model`,`cat_path`,`num_iid`,`syn_flg`,`created`,`creater`,`modified`,`modifier`
from cms_bt_promotion_groups



        AND `id` = #{id}



        AND `promotion_id` = #{promotionId}



        AND `org_channel_id` = #{orgChannelId}



        AND `model_id` = #{modelId}



        AND `product_model` = #{productModel}



        AND `cat_path` = #{catPath}



        AND `num_iid` = #{numIid}



        AND `syn_flg` = #{synFlg}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
