mapper.select:
select
`id`,`channel_id`,`cart_id`,`promotion_status`,`promotion_name`,`pre_sale_end`,`pre_sale_start`,`pre_period_start`,`pre_period_end`,`activity_start`,`activity_end`,`tejiabao_id`,`promotion_type`,`ref_tag_id`,`is_active`,`is_all_promotion`,`created`,`creater`,`modified`,`modifier`
from cms_bt_promotion


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `cart_id` = #{cartId}


        AND `promotion_status` = #{promotionStatus}


        AND `promotion_name` = #{promotionName}


        AND `pre_sale_end` = #{preSaleEnd}


        AND `pre_sale_start` = #{preSaleStart}


        AND `pre_period_start` = #{prePeriodStart}


        AND `pre_period_end` = #{prePeriodEnd}


        AND `activity_start` = #{activityStart}


        AND `activity_end` = #{activityEnd}


        AND `tejiabao_id` = #{tejiabaoId}


        AND `promotion_type` = #{promotionType}


        AND `ref_tag_id` = #{refTagId}


        AND `is_active` = #{isActive}


        AND `is_all_promotion` = #{isAllPromotion}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`channel_id`,`cart_id`,`promotion_status`,`promotion_name`,`pre_sale_end`,`pre_sale_start`,`pre_period_start`,`pre_period_end`,`activity_start`,`activity_end`,`tejiabao_id`,`promotion_type`,`ref_tag_id`,`is_active`,`is_all_promotion`,`created`,`creater`,`modified`,`modifier`
from cms_bt_promotion



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `cart_id` = #{cartId}



        AND `promotion_status` = #{promotionStatus}



        AND `promotion_name` = #{promotionName}



        AND `pre_sale_end` = #{preSaleEnd}



        AND `pre_sale_start` = #{preSaleStart}



        AND `pre_period_start` = #{prePeriodStart}



        AND `pre_period_end` = #{prePeriodEnd}



        AND `activity_start` = #{activityStart}



        AND `activity_end` = #{activityEnd}



        AND `tejiabao_id` = #{tejiabaoId}



        AND `promotion_type` = #{promotionType}



        AND `ref_tag_id` = #{refTagId}



        AND `is_active` = #{isActive}



        AND `is_all_promotion` = #{isAllPromotion}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
