mapper.select:
select
sku,
cart_id,
qty
from   voyageone_cms2.cms_bt_stock_sales_quantity
where channel_id = #{channelId}

        and cart_id = #{cartId}


        and sku = #{sku}


        and (sku in

            #{sku}

        )


        and end_flg = #{endFlg}


         and start_time <= #{sysTime}

select
sum(qty) as qty_sum
from   voyageone_cms2.cms_bt_stock_sales_quantity
where channel_id = #{channelId}

    and cart_id = #{cartId}


    and sku = #{sku}


    and end_flg = #{endFlg}


     and start_time <= #{sysTime}

mapper.update:
update voyageone_cms2.cms_bt_stock_sales_quantity


        qty = #{qty},


        end_flg = #{endFlg},

    modified = now(),
    modifier = #{modifier}


    1 = 1

        and channel_id = #{channelId}


        and cart_id = #{cartId}


        and sku = #{sku}


        and (sku in

            #{skuItem}

        )
