mapper.select:
select t1.sub_task_id, t1.task_id, t1.sub_task_name, t1.cart_id, t1.type, t1.value
,t2.name
from voyageone_cms2.cms_bt_stock_separate_increment_task t1
left outer join (select value,name from com_mt_value_channel where type_id= '53'and channel_id = #{channelId} and lang_id = #{lang}) t2 on t1.cart_id = t2.value
where 1=1
and sub_task_id = #{subTaskId}
and (sub_task_id in
#{subTaskIdListItem}
)
and task_id = #{taskId}
and cart_id = #{cartId}
and sub_task_name like '%${subTaskName}%'

mapper.delete:
delete
from voyageone_cms2.cms_bt_stock_separate_increment_task
where 1=1

        and sub_task_id = #{subTaskId}


        and task_id = #{taskId}
