mapper.select:
select product_model, product_code, sku, cart_id, property1, property2, property3, property4, qty, separate_qty, t2.name as status_name from ${tableName} t1 left outer join (select value,name from com_mt_value where type_id= '63' and lang_id = #{lang}) t2 on t1.status = t2.value where task_id = #{taskId}
and (sku in
#{sku}
)
order by sku,cart_id

select count(*)
   from   voyageone_cms2.cms_bt_stock_separate_item
   where task_id = #{task_id}

       and status = #{status}
