mapper.select:
select
    task_id,
    product_model,
    product_code,
    sku,
    cart_id,
    property1,
    property2,
    property3,
    property4,
    qty,
    separate_qty,
    status
from voyageone_cms2.cms_bt_stock_separate_item${tableNameSuffix}
where 1=1

    and task_id = #{taskId}


    and cart_id = #{cartId}


    and sku = #{sku}


    and (sku in

        #{skuItem}

    )


    and channel_id = #{channelId}


    and status = #{status}


    and (status in

        #{statusItem}

    )

select sum(separate_qty) as separate_qty_sum
from   voyageone_cms2.cms_bt_stock_separate_item
where
sku = #{sku}
and channel_id = #{channelId}

    and status = #{status}


    and (status in

        #{statusItem}

    )


    and task_id = #{taskId}


    and cart_id = #{cartId}

select seq
    from   voyageone_cms2.cms_bt_stock_separate_item
    where task_id = #{taskId}

        and (status in

            #{statusItem}

        )

limit 0,1

mapper.update:
update voyageone_cms2.cms_bt_stock_separate_item


        qty = #{qty},


        separate_qty = #{separateQty},


        error_msg = #{errorMsg},


        error_time = #{errorTime},


        separate_time = #{separateTime},


        restore_time = #{restoreTime},


        status = #{status},

    modified = now(),

        modifier = #{modifier},



    1 = 1

        and seq = #{seq}


        and task_id = #{taskId}


        and sku = #{sku}


        and cart_id = #{cartId}


        and (status in

            #{statusItem}

        )

mapper.delete:
delete
from voyageone_cms2.cms_bt_stock_separate_item
where
task_id = #{taskId}

    and cart_id = #{cartId}


    and sku = #{sku}

mapper.select:
(select
'一般库存隔离' as type,
t2.task_name as taskName,
t1.separate_qty as qty
from voyageone_cms2.cms_bt_stock_separate_item${tableNameSuffix} t1
inner join voyageone_cms2.cms_bt_tasks t2 on t1.task_id = t2.task_id
where
t1.task_id = #{taskId}
and t1.cart_id = #{cartId}
and t1.sku = #{sku}
and (t1.status in

        #{statusItem}

    )
)
union
(select
    '增量库存隔离' as type,
    t2.sub_task_Name as taskName,
    t1.increment_qty as qty
from voyageone_cms2.cms_bt_stock_separate_increment_item${tableNameSuffix} t1
inner join voyageone_cms2.cms_bt_stock_separate_increment_task t2 on t1.sub_task_id = t2.sub_task_id
where
t2.task_id = #{taskId}
and t2.cart_id = #{cartId}
and t1.sku = #{sku}
and (t1.status in

        #{statusItem}

    )
)
