mapper.select:
select
`id`,`channel_id`,`tag_name`,`tag_path`,`tag_path_name`,`tag_type`,`tag_status`,`sort_order`,`parent_tag_id`,`active`,`created`,`creater`,`modified`,`modifier`
from cms_bt_tag


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `tag_name` = #{tagName}


        AND `tag_path` = #{tagPath}


        AND `tag_path_name` = #{tagPathName}


        AND `tag_type` = #{tagType}


        AND `tag_status` = #{tagStatus}


        AND `sort_order` = #{sortOrder}


        AND `parent_tag_id` = #{parentTagId}


        AND `active` = #{active}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`channel_id`,`tag_name`,`tag_path`,`tag_path_name`,`tag_type`,`tag_status`,`sort_order`,`parent_tag_id`,`active`,`created`,`creater`,`modified`,`modifier`
from cms_bt_tag



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `tag_name` = #{tagName}



        AND `tag_path` = #{tagPath}



        AND `tag_path_name` = #{tagPathName}



        AND `tag_type` = #{tagType}



        AND `tag_status` = #{tagStatus}



        AND `sort_order` = #{sortOrder}



        AND `parent_tag_id` = #{parentTagId}



        AND `active` = #{active}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
