mapper.insert:
INSERT INTO voyageone_cms2.cms_bt_tasks (
    task_name, task_type, promotion_id, activity_start, activity_end,

      config,

    created, creater, modified, modifier
) VALUES (
    #{task_name}, #{task_type}, #{promotion_id}, #{activity_start}, #{activity_end}, #{config}, now(), #{creater}, now(), #{modifier}
)

mapper.update:
UPDATE voyageone_cms2.cms_bt_tasks
SET task_name = #{task_name},
config = #{config},
modified = NOW(),
    modifier  = #{modifier}
WHERE task_id = #{task_id}
      AND modified = #{modified}

mapper.select:
SELECT task_id, task_name, task_type, promotion_id, config, created, creater, modified, modifier FROM voyageone_cms2.cms_bt_tasks WHERE promotion_id = #{promotion_id}
AND task_name = #{task_name}
AND task_type = #{task_type}
