mapper.select:
select
`id`,`channel_id`,`config_key`,`config_code`,`config_value1`,`config_value2`,`config_value3`,`comment`,`created`,`creater`,`modified`,`modifier`,`status`,`config_type`
from cms_mt_channel_config


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `config_key` = #{configKey}


        AND `config_code` = #{configCode}


        AND `config_value1` = #{configValue1}


        AND `config_value2` = #{configValue2}


        AND `config_value3` = #{configValue3}


        AND `comment` = #{comment}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}


        AND `status` = #{status}


        AND `config_type` = #{configType}

select
`id`,`channel_id`,`config_key`,`config_code`,`config_value1`,`config_value2`,`config_value3`,`comment`,`created`,`creater`,`modified`,`modifier`,`status`,`config_type`
from cms_mt_channel_config



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `config_key` = #{configKey}



        AND `config_code` = #{configCode}



        AND `config_value1` = #{configValue1}



        AND `config_value2` = #{configValue2}



        AND `config_value3` = #{configValue3}



        AND `comment` = #{comment}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}



        AND `status` = #{status}



        AND `config_type` = #{configType}


limit 1
