mapper.select:
select
`id`,`prop_id`,`prop_parent_id`,`prop_name`,`prop_type`,`action_type`,`platform_prop_ref_id`,`rules`,`defult`,`is_comm`,`is_code`,`tips`,`value_type`,`created`,`creater`,`modified`,`modifier`
from cms_mt_common_prop


        AND `id` = #{id}


        AND `prop_id` = #{propId}


        AND `prop_parent_id` = #{propParentId}


        AND `prop_name` = #{propName}


        AND `prop_type` = #{propType}


        AND `action_type` = #{actionType}


        AND `platform_prop_ref_id` = #{platformPropRefId}


        AND `rules` = #{rules}


        AND `defult` = #{defult}


        AND `is_comm` = #{isComm}


        AND `is_code` = #{isCode}


        AND `tips` = #{tips}


        AND `value_type` = #{valueType}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`prop_id`,`prop_parent_id`,`prop_name`,`prop_type`,`action_type`,`platform_prop_ref_id`,`rules`,`defult`,`is_comm`,`is_code`,`tips`,`value_type`,`created`,`creater`,`modified`,`modifier`
from cms_mt_common_prop



        AND `id` = #{id}



        AND `prop_id` = #{propId}



        AND `prop_parent_id` = #{propParentId}



        AND `prop_name` = #{propName}



        AND `prop_type` = #{propType}



        AND `action_type` = #{actionType}



        AND `platform_prop_ref_id` = #{platformPropRefId}



        AND `rules` = #{rules}



        AND `defult` = #{defult}



        AND `is_comm` = #{isComm}



        AND `is_code` = #{isCode}



        AND `tips` = #{tips}



        AND `value_type` = #{valueType}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
