mapper.select:
select
`id`,`channel_id`,`feed_cat_path`,`feed_prop_original`,`feed_prop_translation`,`display_order`,`created`,`creater`,`modified`,`modifier`
from cms_mt_feed_custom_prop


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `feed_cat_path` = #{feedCatPath}


        AND `feed_prop_original` = #{feedPropOriginal}


        AND `feed_prop_translation` = #{feedPropTranslation}


        AND `display_order` = #{displayOrder}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select
`id`,`channel_id`,`feed_cat_path`,`feed_prop_original`,`feed_prop_translation`,`display_order`,`created`,`creater`,`modified`,`modifier`
from cms_mt_feed_custom_prop



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `feed_cat_path` = #{feedCatPath}



        AND `feed_prop_original` = #{feedPropOriginal}



        AND `feed_prop_translation` = #{feedPropTranslation}



        AND `display_order` = #{displayOrder}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
