mapper.select:
select `id`,`channel_id`,`cart_id`,`cfg_type`,`cfg_name`,`cfg_val`,`created`,`creater`,`modified`,`modifier`
from cms_mt_platform_config


        AND `id` = #{id}


        AND `channel_id` = #{channelId}


        AND `cart_id` = #{cartId}


        AND `cfg_type` = #{cfgType}


        AND `cfg_name` = #{cfgName}


        AND `cfg_val` = #{cfgVal}


        AND `created` = #{created}


        AND `creater` = #{creater}


        AND `modified` = #{modified}


        AND `modifier` = #{modifier}

select `id`,`channel_id`,`cart_id`,`cfg_type`,`cfg_name`,`cfg_val`,`created`,`creater`,`modified`,`modifier`
from cms_mt_platform_config



        AND `id` = #{id}



        AND `channel_id` = #{channelId}



        AND `cart_id` = #{cartId}



        AND `cfg_type` = #{cfgType}



        AND `cfg_name` = #{cfgName}



        AND `cfg_val` = #{cfgVal}



        AND `created` = #{created}



        AND `creater` = #{creater}



        AND `modified` = #{modified}



        AND `modifier` = #{modifier}


limit 1
