mapper.select:
select * from cms_bt_jm_promotion
active=1
AND id = #{id}
and channel_id=#{channelId}
and name like CONCAT(#{name},'%')
and cms_bt_jm_master_brand_Id = #{cmsBtJmMasterBrandId}
Created>=#{beginCreated}
Created<=#{endCreated}
and ( -- 未开始 activity_start>now()
-- 进行中 or (activity_start < now() and activity_end >now())
-- 进行中 (activity_start < now() and activity_end >now())
-- 完成 or ( activity_end <now())
-- 完成 ( activity_end <now())
)
order by id desc

select DISTINCT a.*, b.ref_tag_id  from cms_bt_jm_promotion a left join cms_bt_promotion b on a.id = b.promotion_id
inner join voyageone_cms2.cms_bt_tag c on c.parent_tag_id = b.ref_tag_id  and (c.channel_id = #{channelId}

    or c.channel_id = #{orgChannelId}

)
where
    (a.channel_id = #{channelId}

        or a.channel_id = #{orgChannelId}

    )
    and b.activity_end >= now()
    and b.promotion_status = 1
    and b.active = 1
    and a.active = 1
    and c.tag_status = 0
    and c.active = 1
    and b.cart_id = #{cartId,jdbcType=INTEGER}
    order by id desc

select jmprom.*, prom.id as promId from cms_bt_jm_promotion as jmprom INNER JOIN cms_bt_promotion as prom
on prom.promotion_id = jmprom.id

    INNER JOIN cms_bt_jm_promotion_sku as jmpromsku
    on jmpromsku.cms_bt_jm_promotion_id = jmprom.id and jmpromsku.channel_id = #{channelId}


    prom.active = 1 and jmprom.active = 1

        AND jmprom.id = #{jmpromId}

    and jmprom.channel_id = #{channelId}

        and jmprom.name like concat('%', #{jmpromName},'%')


        and jmprom.cms_bt_jm_master_brand_Id = #{jmBrandId}


        and jmprom.prod_sum #{compareType} #{prodSum}


        and jmprom.pre_period_start <= #{prePeriodEnd}



            and #{prePeriodStart} <= jmprom.pre_period_start


            and (#{prePeriodStart} <= jmprom.pre_period_start and jmprom.pre_period_start <= #{prePeriodEnd})



        and jmprom.mainCata = #{mainCata}


        and jmprom.activity_end <= #{activityEnd}



            and #{activityStart} <= jmprom.activity_start


            and ( #{activityStart} <= jmprom.activity_start and jmprom.activity_end <= #{activityEnd} )



        and ( jmpromsku.product_code in (#{codeListStr}) or jmpromsku.sku_code in (#{codeListStr}) or jmpromsku.product_model in (#{codeListStr}) )


order by id desc

select count(jmprom.id) from cms_bt_jm_promotion as jmprom INNER JOIN cms_bt_promotion as prom on prom.promotion_id = jmprom.id
prom.active = 1 and jmprom.active = 1
AND jmprom.id = #{id}
and jmprom.channel_id=#{channelId}
and jmprom.name like CONCAT(#{name},'%')
and jmprom.cms_bt_jm_master_brand_Id = #{cmsBtJmMasterBrandId}
jmprom.Created>=#{beginCreated}
jmprom.Created<=#{endCreated}
and ( -- 未开始 jmprom.activity_start>now()
-- 进行中 or (jmprom.activity_start < now() and jmprom.activity_end >now())
-- 进行中 (jmprom.activity_start < now() and jmprom.activity_end >now())
-- 完成 or ( jmprom.activity_end <now())
-- 完成 ( jmprom.activity_end <now())
)
