mapper.select:
select * from cms_bt_jm_promotion
active=1
AND id = #{id}
and channel_id=#{channelId}
and name like CONCAT(#{name},'%')
and cms_bt_jm_master_brand_Id = #{cmsBtJmMasterBrandId}
Created>=#{beginCreated}
Created<=#{endCreated}
and ( -- 未开始 activity_start>now()
-- 进行中 or (activity_start < now() and activity_end >now())
-- 进行中 (activity_start < now() and activity_end >now())
-- 完成 or ( activity_end <now())
-- 完成 ( activity_end <now())
)
order by id desc

select DISTINCT a.*, b.ref_tag_id  from cms_bt_jm_promotion a left join cms_bt_promotion b on a.id = b.promotion_id
inner join voyageone_cms2.cms_bt_tag c on c.parent_tag_id = b.ref_tag_id  and (c.channel_id = #{channelId}

    or c.channel_id = #{orgChannelId}

)
where
    (a.channel_id = #{channelId}

        or a.channel_id = #{orgChannelId}

    )
    and b.activity_end >= now()
    and b.promotion_status = 1
    and b.active = 1
    and a.active = 1
    and c.tag_status = 0
    and c.active = 1
    and b.cart_id = #{cartId,jdbcType=INTEGER}
    order by id desc

select product_code , jm_hash_id from cms_bt_jm_promotion_product a where exists (select 1 from (select max(id) as id from cms_bt_jm_promotion_product where channel_id = #{channelId} and jm_hash_id != ''
and product_code = #{productCode}
group by product_code) as b where a.id=b.id)

select product_code , jm_hash_id from cms_bt_jm_promotion_product where channel_id = #{channelId} and jm_hash_id != ''
and product_code = #{productCode}
ORDER BY jm_hash_id desc LIMIT 0,1

select product_code , max(origin_jm_hash_id) as jm_hash_id from cms_bt_jm_product where channel_id = #{channelId} and origin_jm_hash_id != ''
and product_code = #{productCode}
group by product_code

select DISTINCT jmprom.*, prom.id as promId, jmpromext.app_page_id, jmpromext.pc_page_id, jmpromext.main_title
from cms_bt_jm_promotion as jmprom INNER JOIN cms_bt_promotion as prom
on prom.promotion_id = jmprom.id

    INNER JOIN cms_bt_jm_promotion_sku as jmpromsku
    on jmpromsku.cms_bt_jm_promotion_id = jmprom.id and jmpromsku.channel_id = #{channelId}

left JOIN cms_bt_jm_promotion_special_extension as jmpromext
on jmpromext.jmpromotion_id = jmprom.id

    and jmpromext.main_channel = #{mainCata}


    prom.active = 1 and jmprom.active = 1
    and jmprom.channel_id = #{channelId}

        AND ( jmprom.activity_pc_id = #{jmActId} or jmprom.activity_app_id = #{jmActId} )


        and jmprom.name like concat('%', #{jmpromName},'%')


        and jmprom.master_brand_name IN (

            #{brandName}

        )


        and jmprom.prod_sum < #{prodSum}


        and jmprom.prod_sum > #{prodSum}


        and jmprom.prod_sum = #{prodSum}


        and jmprom.pre_period_start <= #{prePeriodEnd}



            and #{prePeriodStart} <= jmprom.pre_period_start


            and (#{prePeriodStart} <= jmprom.pre_period_start and jmprom.pre_period_start <= #{prePeriodEnd})



        and jmprom.activity_start <= #{activityEnd}



            and #{activityStart} <= jmprom.activity_end


            and ( jmprom.activity_start <= #{activityEnd} and #{activityStart} <= jmprom.activity_end )



        and ( jmpromsku.product_code in ( #{item} )
             or jmpromsku.sku_code in ( #{item} )
             or jmpromsku.product_model in ( #{item} ) )


order by id desc
LIMIT #{offset}, #{size}

select count(DISTINCT jmprom.id) from cms_bt_jm_promotion as jmprom INNER JOIN cms_bt_promotion as prom
on prom.promotion_id = jmprom.id

    INNER JOIN cms_bt_jm_promotion_sku as jmpromsku
    on jmpromsku.cms_bt_jm_promotion_id = jmprom.id and jmpromsku.channel_id = #{channelId}

left JOIN cms_bt_jm_promotion_special_extension as jmpromext
on jmpromext.jmpromotion_id = jmprom.id

    and jmpromext.main_channel = #{mainCata}


    prom.active = 1 and jmprom.active = 1
    and jmprom.channel_id = #{channelId}

        AND ( jmprom.activity_pc_id = #{jmActId} or jmprom.activity_app_id = #{jmActId} )


        and jmprom.name like concat('%', #{jmpromName},'%')


        and jmprom.master_brand_name IN (

            #{brandName}

        )


        and jmprom.prod_sum < #{prodSum}


        and jmprom.prod_sum > #{prodSum}


        and jmprom.prod_sum = #{prodSum}


        and jmprom.pre_period_start <= #{prePeriodEnd}



            and #{prePeriodStart} <= jmprom.pre_period_start


            and (#{prePeriodStart} <= jmprom.pre_period_start and jmprom.pre_period_start <= #{prePeriodEnd})



        and jmprom.activity_start <= #{activityEnd}



            and #{activityStart} <= jmprom.activity_end


            and ( jmprom.activity_start <= #{activityEnd} and #{activityStart} <= jmprom.activity_end )



        and ( jmpromsku.product_code in ( #{item} )
        or jmpromsku.sku_code in ( #{item} )
        or jmpromsku.product_model in ( #{item} ) )

SELECT a.`id`,a.`name`,a.`ref_tag_id`,COUNT(c.id) AS productCount FROM cms_bt_jm_promotion a JOIN cms_bt_promotion b ON a.id = b.promotion_id left JOIN `cms_bt_jm_promotion_product` AS c ON a.`id`=c.`cms_bt_jm_promotion_id` AND c.`product_code` IN #{item}
WHERE a.channel_id =#{channelId}
AND b.activity_end >= #{activityStart}
AND b.activity_end >= now()
AND #{activityEnd} >= b.activity_end
AND b.promotion_status = 1 AND b.active = 1 AND a.active = 1 AND b.cart_id =#{cartId}
GROUP BY a.id ORDER BY a.id DESC
