SELECT a.* FROM `cms_bt_jm_promotion_product` AS a JOIN cms_bt_jm_promotion AS b ON a.cms_bt_jm_promotion_id=b.id
      WHERE b.promotion_type=1 AND a.synch_status=2 AND a.channel_id=#{channelId} and   a.product_code=#{productCode}
              AND  #{activityStart} <=a.`activity_end`  and   #{activityEnd}>=a.`activity_start`
              AND a.cms_bt_jm_promotion_id!=#{cmsBtJmPromotionId}
               LIMIT 1

SELECT b.`jumei_product_id`,b.`origin_jm_hash_id`,b.`jumei_mall_id`,b.`origin`, b.`product_name_cn`,b.`vo_category_name`,b.`vo_brand_name`,b.`brand_name`,b.`product_type`, b.`size_type`,b.`product_des_en`,b.`attribute`,b.`address_of_produce`, b.`available_period`,b.`product_des_cn`,b.`applicable_crowd`,b.`specialNote`,b.`color_en`, b.`creater`,b.`modified`,b.`modifier`,b.`created`,b.`product_long_name`,b.`product_medium_name`, b.`product_short_name`,b.`search_meta_text_custom`,b.`msrp_usd`,b.`msrp_rmb`,b.`retail_price`, b.`sale_price`,b.`hs_code`,b.`hs_name`,b.`hs_unit`,b.`material_en`,b.`material_cn`,b.`category_name`, b.`comment`,IFNULL(b.`image1`,a.`image1`),IFNULL(b.`foreign_language_name`,a.`product_name_en`) AS foreign_language_name,a.`product_code`,a.`channel_id`,a.`app_id`,a.`pc_id`,a.`limit`,a.promotion_tag,a.jm_hash_id,b.jumei_mall_id, a.error_msg FROM `cms_bt_jm_promotion_product` AS a Left JOIN `cms_bt_jm_product` AS b ON a.`channel_id`=b.`channel_id` AND a.`product_code`=b.`product_code` WHERE a.`cms_bt_jm_promotion_id`= #{promotionId}
and a.product_code IN
#{item}

mapper.update:
UPDATE cms_bt_jm_promotion_product AS a JOIN (SELECT cms_bt_jm_promotion_product_id,AVG(market_price) AS market_price,AVG(deal_price) AS deal_price,AVG(`discount`) AS discount ,max(market_price) AS max_market_price,max(deal_price) AS max_deal_price,min(market_price) AS min_market_price,min(deal_price) AS min_deal_price FROM `cms_bt_jm_promotion_sku` WHERE cms_bt_jm_promotion_product_id in #{item}
group by cms_bt_jm_promotion_product_id ) AS b ON a.id=b.cms_bt_jm_promotion_product_id SET a.deal_Price=b.deal_price,a.market_price=b.market_price,a.discount=b.discount ,a.max_deal_Price=b.max_deal_price,a.max_market_price=b.max_market_price ,a.min_deal_Price=b.min_deal_price,a.min_market_price=b.min_market_price ,a.price_status=(case WHEN a.synch_status=2 then 1 else a.price_status end ) WHERE a.id IN
#{item}

UPDATE cms_bt_jm_promotion_product set price_status=1,error_msg=''
WHERE    id in

    #{item}

UPDATE cms_bt_jm_promotion_product set synch_status=1,error_msg=''
WHERE   synch_status in (0,3) and  id in

    #{item}
;

UPDATE cms_bt_jm_promotion_product set price_status=1,error_msg=''
WHERE    id in  #{item}
and synch_status=2;

UPDATE cms_bt_jm_promotion_product set price_status=1
,error_msg=''
WHERE    cms_bt_jm_promotion_id=#{promotionId} and synch_status in (2);

UPDATE cms_bt_jm_promotion_product set synch_status=1
,error_msg=''
WHERE    cms_bt_jm_promotion_id=#{promotionId} and synch_status in (0,3);

mapper.delete:
