 WHERE a.channel_id=#{channelId} and a.cms_bt_jm_promotion_id!=#{cmsBtJmPromotionId}   AND  #{nowDate} <=a.`activity_end`  AND   #{nowDate}>=a.`activity_start` AND a.`synch_status`=2 limit 1

