mapper.select:
select DISTINCT log.id, log.channel_id, log.product_id, log.code, log.sku, log.msrp_price, log.retail_price, log.sale_price, log.client_msrp_price, log.client_retail_price, log.client_net_price, log.comment, log.created, log.creater, log.modified, log.modifier from voyageone_cms2.cms_bt_price_log log where log.code = #{code}
and log.sku = #{sku}
and log.channel_id = #{channelId} order by log.modified desc limit #{offset}, #{rows}

SELECT count(1)
FROM voyageone_cms2.cms_bt_price_log log
WHERE log.code = #{code} AND log.sku = #{sku} AND log.channel_id = #{channelId}

SELECT id, channel_id, product_id, cart_id, code, sku, msrp_price, retail_price, sale_price, client_msrp_price, client_retail_price, client_net_price, comment, created, creater, modified, modifier FROM voyageone_cms2.cms_bt_price_log WHERE channel_id = #{channel_id} and cart_id = #{cart_id} and code = #{code}
and sku = #{sku}
LIMIT #{offset}, #{limit}

SELECT
    COUNT(1)
FROM voyageone_cms2.cms_bt_price_log
WHERE channel_id = #{channel_id}
    and cart_id = #{cart_id}
    and code = #{code}

        and sku = #{sku}
