mapper.select:
SELECT t1.*, count(1) AS skuCount,MAX(t2.`msrp_rmb`) AS maxMsrpRmb,MIN(t2.`msrp_rmb`) AS minMsrpRmb, MAX(t2.`retail_price`) AS maxRetailPrice,MIN(t2.`retail_price`) AS minRetailPrice, MAX(t2.`sale_price`) AS maxSalePrice,MIN(t2.`sale_price`) AS minSalePrice, MAX(t2.`promotion_price`) AS maxPromotionPrice,MIN(t2.`promotion_price`) AS minPromotionPrice, MAX(t2.`msrp_usd`) AS maxMsrpUsd,MIN(t2.`msrp_usd`) AS minMsrpUsd FROM ( SELECT a.id, a.promotion_id, a.org_channel_id, d.full_name, a.product_id, a.model_id, a.product_model, a.product_code, a.product_name, a.cat_path, a.retail_price, a.sale_price, a.msrp, a.tag_id, a.promotion_price, a.num_iid, a.image_url_1, a.image_url_2, a.image_url_3, a.property_1, a.property_2, a.property_3, a.property_4, a.created, a.creater, a.modified, a.modifier, a.image_url_1 as image, b.tag_path, b.tag_name, b.tag_path_name FROM voyageone_cms2.cms_bt_promotion_codes a LEFT JOIN voyageone_cms2.cms_bt_tag b ON a.tag_id = b.id LEFT JOIN tm_order_channel d on a.org_channel_id = d.order_channel_id
a.promotion_id = #{promotionId,jdbcType=INTEGER}
and a.org_channel_id = #{channelId,jdbcType=VARCHAR}
and a.model_id = #{modelId,jdbcType=INTEGER}
and a.product_model = #{productModel}
and ( a.product_model like '%${key}%' or a.product_code like '%${key}%' or a.product_name like '%${key}%' )
LIMIT #{start}, #{length}
) t1 LEFT JOIN voyageone_cms2.cms_bt_promotion_skus t2 ON t1.promotion_id = t2.promotion_id AND t1.product_code = t2.product_code GROUP BY t1.promotion_id,t1.product_code

SELECT
count(1)
FROM
voyageone_cms2.cms_bt_promotion_codes


        promotion_id = #{promotionId,jdbcType=INTEGER}


        and org_channel_id = #{channelId,jdbcType=VARCHAR}


        and model_id = #{modelId,jdbcType=INTEGER}


        and (

        product_model like '%${key}%'
        or product_code like '%${key}%'
        or product_name like '%${key}%'
        )

SELECT a.id, a.promotion_id, a.org_channel_id, a.product_id, a.model_id, a.product_model, a.product_code, a.product_name, a.cat_path, c.retail_price, c.sale_price, c.msrp_rmb as msrp, c.msrp_usd as msrpUS, a.tag_id, c.promotion_price, a.num_iid, a.image_url_1, a.image_url_2, a.image_url_3, a.property_1, a.property_2, a.property_3, a.property_4, a.created, a.creater, a.modified, a.modifier, a.image_url_1 as image, -- b.tag_path, GROUP_CONCAT(b.tag_name SEPARATOR '|') as tag_name, -- b.tag_path_name, c.product_sku, c.size FROM voyageone_cms2.cms_bt_promotion_codes a -- LEFT JOIN voyageone_cms2.cms_bt_tag b ON a.tag_id = b.id left join voyageone_cms2.`cms_bt_promotion_codes_tag` as b on a.id=b.cms_bt_promotion_codes_id LEFT JOIN voyageone_cms2.cms_bt_promotion_skus c on a.promotion_id = c.promotion_id and a.product_code = c.product_code
a.promotion_id = #{promotionId,jdbcType=INTEGER}
and a.org_channel_id= #{orgChannelId,jdbcType=VARCHAR}
group by a.id,c.id

SELECT DISTINCT(promotion_name) from voyageone_cms2.cms_bt_promotion_codes t1
INNER JOIN voyageone_cms2.cms_bt_promotion t2 on  t1.promotion_id = t2.id
WHERE t2.channel_id = #{channelId,jdbcType=VARCHAR}

    AND t2.cart_id = #{cartId,jdbcType=INTEGER}

AND t1.product_code = #{productCode,jdbcType=VARCHAR}
AND IF(LENGTH(t2.activity_end) = 10,CONCAT(t2.activity_end," 23:59:59"),t2.activity_end) >= #{now,jdbcType=VARCHAR}
AND t2.active = 1
AND t2.is_all_promotion = 0

mapper.delete:
delete
from voyageone_cms2.cms_bt_promotion_codes
where
promotion_id = #{promotionId,jdbcType=INTEGER}

    and product_id = #{productId,jdbcType=INTEGER}


    and product_model = #{productModel,jdbcType=VARCHAR}


    and product_code = #{productCode,jdbcType=VARCHAR}
