mapper.select:
select

d.status,
b.name as cartName,
c.id as tag_id,
c.channel_id as tag_channel_id,
c.tag_name,
c.tag_path,
c.tag_path_name,
c.tag_type,
c.tag_status,
c.sort_order,
c.parent_tag_id
from voyageone_cms2.cms_bt_promotion a left join Synship.ct_cart b on a.cart_id = b.cart_id
left join voyageone_cms2.cms_bt_tag c on c.parent_tag_id = a.ref_tag_id  and (c.channel_id = #{channelId}

    or c.channel_id = #{orgChannelId}

)
left join voyageone_cms2.cms_bt_jm_promotion d on a.promotion_id = d.id and a.channel_id = d.channel_id
where
a.active = 1
and (a.channel_id = #{channelId}

    or a.channel_id = #{orgChannelId}

)


    and a.id = #{promotionId}


    and a.promotion_name LIKE '%${promotionName}%'


    and a.promotion_status = #{promotionStatus}


    and a.cart_id = #{cartId,jdbcType=INTEGER}


    and a.promotion_type = #{promotionType,jdbcType=VARCHAR}


    and a.created >= #{createdStart,jdbcType=VARCHAR}


    and #{createdEnd,jdbcType=VARCHAR} >= a.created

order by  id desc

select
a.id,
a.promotion_name,
a.activity_start,
a.activity_end,
a.ref_tag_id
from voyageone_cms2.cms_bt_promotion a
inner join voyageone_cms2.cms_bt_tag c on c.parent_tag_id = a.ref_tag_id  and (c.channel_id = #{channelId}

        or c.channel_id = #{orgChannelId}

    )
where
a.active = 1
and a.promotion_status = 0
and c.active = 1
and c.tag_status = 0
and a.cart_id = #{cartId,jdbcType=INTEGER}
and (a.channel_id = #{channelId}

    or a.channel_id = #{orgChannelId}

)
order by id desc

SELECT a.id as seq, a.promotion_id as promotionId, b.channel_id as channelId, b.cart_id as cartId, b.name, b.add_name2 as shortName, b.promotion_status as promotionStatus, b.promotion_name as promotionName, b.activity_start as activityStart, b.activity_end as activityEnd, a.product_id as productId, a.product_code as productCode, a.sale_price as salePrice, a.msrp, a.promotion_price as promotionPrice, a.created, a.creater, a.modified, a.modifier FROM voyageone_cms2.cms_bt_promotion_codes a INNER JOIN ( SELECT p.id, p.channel_id, p.cart_id, p.promotion_status, p.promotion_name, p.activity_start, p.activity_end, c.name, c.add_name2 FROM voyageone_cms2.cms_bt_promotion p INNER JOIN Synship.com_mt_value_channel c ON p.cart_id = c.value and c.type_id = '53' and c.lang_id = #{lang} and p.channel_id = c.channel_id) b ON a.promotion_id = b.id WHERE a.product_code = #{code} AND b.channel_id = #{channelId}
and b.cart_id = #{cartId}
LIMIT #{offset}, #{rows}
