mapper.select:
SELECT id as id, channel_id as channelId, group_id as groupId, cart_id as cartId, publish_status as publishStatus, creater, MAX(created) as created, modifier, MAX(modified) as modified FROM voyageone_cms2.cms_bt_sx_workload WHERE (publish_status=0 or publish_status=3)
AND channel_id=#{channel_id}
AND cart_id=#{cartId}
group by channel_id, cart_id, group_id order by MAX(modified) asc
limit 0,#{record_count}

SELECT id as id, channel_id as channelId, group_id as groupId, cart_id as cartId, publish_status as publishStatus, creater, MAX(created) as created, modifier, MAX(modified) as modified FROM voyageone_cms2.cms_bt_sx_workload WHERE (publish_status=0 or publish_status=3)
AND channel_id=#{channel_id}
AND cart_id=#{cartId}
group by channel_id, cart_id, group_id order by MAX(modified) asc
limit 0,#{record_count}

SELECT id as id, channel_id as channelId, group_id as groupId, cart_id as cartId, publish_status as publishStatus, creater, MAX(created) as created, modifier, MAX(modified) as modified FROM voyageone_cms2.cms_bt_sx_workload WHERE (publish_status=3)
AND channel_id IN #{channelId}
AND cart_id=#{cartId}
group by channel_id, cart_id, group_id order by MAX(modified) asc
limit 0,#{record_count}

mapper.update:
UPDATE
    voyageone_cms2.cms_bt_sx_workload
SET
    publish_status=#{publish_status}
WHERE
id=#{id}


UPDATE
voyageone_cms2.cms_bt_sx_workload
SET
modifier=#{modifier},
modified=#{created}
WHERE
modified <= #{modified}
AND
channel_id =  #{channelId}
AND
cart_id =  #{cartId}
AND
group_id =  #{groupId}
AND
 (publish_status=0 or publish_status=3)

mapper.select:
SELECT id as id, channel_id as channelId, cart_id as cartId, workload_name as workloadName, publish_status as publishStatus, group_id as groupId, priority_order as priorityOrder, creater, created, modifier, modified FROM voyageone_cms2.cms_bt_platform_workload WHERE publish_status = 0
AND cart_id in #{cartId}
AND channel_id in #{channelId}
order by priority_order ASC ,created ASC
limit 0,#{record_count}
