mapper.select:
SELECT id as id, channel_id as channelId, group_id as groupId, cart_id as cartId, publish_status as publishStatus, creater, created, modifier, modified FROM voyageone_cms2.cms_bt_sx_workload WHERE publish_status=0
AND channel_id=#{channel_id}
AND cart_id=#{cartId}
group by channel_id, cart_id, group_id order by modified desc
limit 0,#{record_count}

mapper.update:
UPDATE
    voyageone_cms2.cms_bt_sx_workload
SET
    publish_status=#{publish_status}
WHERE
id=#{id}
