select tag.id, tag.tag_name, tag.channel_id, tag.tag_path, tag.tag_path_name, tag.tag_type, tag.tag_status, tag.sort_order, tag.parent_tag_id, tag.created, tag.creater, tag.modified, tag.modifier from voyageone_cms2.cms_bt_tag tag where parent_tag_id = #{parentTagId}
and tag_name = #{tagName}
and channel_id = #{channelId} and tag.active = 1

select tag.id, tag.tag_name as tagname, tag.channel_id as channelId, tag.tag_path as tagPath, tag.tag_path_name as tagPathName, tag.tag_path_name as tagChildrenName, tag.tag_type as tagType, tag.tag_status as tagStatus, tag.sort_order as sortOrder, tag.parent_tag_id as parentTagid, tag.active, tag.created, tag.creater, tag.modified, tag.modifier from voyageone_cms2.cms_bt_tag tag where (tag.channel_id = #{channelId}
or tag.channel_id = #{orgChannelId}
) and tag_type = '4' and tag.tag_path_name != '' and tag.active = 1 and tag.tag_status = 0 order by tag.id

select tag.id, tag.tag_name as tagname, tag.channel_id as channelId, tag.tag_path as tagPath, tag.tag_path_name as tagPathName, tag.tag_path_name as tagChildrenName, tag.tag_type as tagType, tag.tag_status as tagStatus, tag.sort_order as sortOrder, tag.parent_tag_id as parentTagid, tag.active, tag.created, tag.creater, tag.modified, tag.modifier from voyageone_cms2.cms_bt_promotion promo
inner join voyageone_cms2.cms_bt_jm_promotion jmpromo on jmpromo.id = promo.promotion_id
inner join voyageone_cms2.cms_bt_tag tag on tag.id = promo.ref_tag_id where (promo.channel_id = #{channelId}
or promo.channel_id = #{orgChannelId}
)
and promo.cart_id = #{cartId}
and promo.activity_end >= concat(date_format(date_add(CURDATE(), interval -90 day), '%Y-%m-%d'),' 00:00:00') and tag.tag_path_name != '' and (tag.channel_id = #{channelId}
or tag.channel_id = #{orgChannelId}
) and tag_type = '2' order by tag.id

select tag.id, tag.tag_name as tagname, tag.channel_id as channelId, tag.tag_path as tagPath, tag.tag_path_name as tagPathName, tag.tag_path_name as tagChildrenName, tag.tag_type as tagType, tag.tag_status as tagStatus, tag.sort_order as sortOrder, tag.parent_tag_id as parentTagid, tag.active, tag.created, tag.creater, tag.modified, tag.modifier from voyageone_cms2.cms_bt_promotion promo left join voyageone_cms2.cms_bt_tag tag on tag.id = promo.ref_tag_id where (promo.channel_id = #{channelId}
or promo.channel_id = #{orgChannelId}
)
and promo.cart_id = #{cartId}
and promo.active=1 and promo.promotion_status = 1 and tag.active = 1 and tag.tag_status = 0 and tag.tag_path_name != '' and (tag.channel_id = #{channelId}
or tag.channel_id = #{orgChannelId}
) and tag_type = '2' order by tag.id

select DISTINCT tag.id, tag.tag_name as tagname, tag.channel_id as channelId, tag.tag_path as tagPath, tag.tag_path_name as tagPathName, tag.tag_path_name as tagChildrenName, tag.tag_type as tagType, tag.tag_status as tagStatus, tag.sort_order as sortOrder, tag.parent_tag_id as parentTagid, tag.active, tag.created, tag.creater, tag.modified, tag.modifier from voyageone_cms2.cms_bt_tag tag where tag.parent_tag_id in (
#{itemIds}
) and tag.tag_path_name != '' and (tag.channel_id = #{channelId}
or tag.channel_id = #{orgChannelId}
) and tag.active = 1 and tag.tag_status = 0 and tag_type = '2' order by tag.id

SELECT tag.id, tag.tag_name as tagname, tag.channel_id as channelId, tag.tag_path as tagPath, tag.tag_path_name as tagPathName, tag.tag_path_name as tagChildrenName, tag.tag_type as tagType, tag.tag_status as tagStatus, tag.sort_order as sortOrder, tag.parent_tag_id as parentTagid from voyageone_cms2.cms_bt_tag tag WHERE tag.channel_id = #{channelId} and tag.active = 1
and (tag.tag_path in
#{tagPathItem}
)
order by tag.id
