mapper.select:
SELECT DISTINCT a.id, a.order_channel_id as orderChannelId, a.cart_id as catId, b.name as cartName, a.name, a.`value`, a.comment, a.creater, a.created, a.modifier, a.modified FROM voyageone_cms2.cms_mt_dict a INNER JOIN Synship.com_mt_value_channel b ON a.cart_id = b.value and b.type_id = '53' and b.lang_id = #{lang} WHERE a.order_channel_id = #{order_channel_id}
AND a.name like CONCAT('%',#{name},'%' )
AND a.cart_id in (#{cart_id}, 0)
LIMIT #{offset}, #{rows};

SELECT count(DISTINCT a.id) FROM voyageone_cms2.cms_mt_dict a INNER JOIN Synship.com_mt_value_channel b ON a.cart_id = b.value and b.type_id = '53' and b.lang_id = #{lang} WHERE order_channel_id = #{order_channel_id}
AND a.name like CONCAT('%',#{name},'%' )
AND a.cart_id in (#{cart_id}, 0)

mapper.update:
UPDATE voyageone_cms2.cms_mt_dict
SET name = #{name}, value = #{value}, modified = NOW(), modifier = #{modifier}
WHERE id = #{id} AND modified = #{modified}
