mapper.select:
select 

from com_login_log


        and `application` = #{application,jdbcType=VARCHAR}


        and `ip` = #{ip,jdbcType=VARCHAR}



        and `creater` like concat('%', #{creater,jdbcType=VARCHAR}, '%')



        and `created` >= #{startTime,jdbcType=VARCHAR}


        and `created` < #{endTime,jdbcType=VARCHAR}

select count(*) from com_login_log


        and `application` = #{application,jdbcType=VARCHAR}


        and `ip` = #{ip,jdbcType=VARCHAR}



        and `creater` like concat('%', #{creater,jdbcType=VARCHAR}, '%')



        and `created` >= #{startTime,jdbcType=VARCHAR}


        and `created` < #{endTime,jdbcType=VARCHAR}
