mapper.select:
SELECT * FROM (SELECT re.*, u.user_account from com_resource re, com_res_role rr, com_role r, com_user_role ur, com_user u, Synship.ct_application ap WHERE re.active =1 AND r.active =1 AND u.active =1 AND ap.active = 1 AND re.id = rr.res_id AND rr.role_id = r.id AND r.id = ur.role_id AND ur.user_id = u.id AND re.application = ap.application GROUP BY re.id, u.user_account
UNION
SELECT re.* ,u.user_account from com_resource re , com_role r, com_role_config rc, com_user_role ur, com_user u , Synship.ct_application ap WHERE re.active =1 AND r.active =1 AND u.active =1 AND ap.active = 1 AND r.id = ur.role_id AND ur.user_id = u.id AND rc.role_id = r.id AND re.application = ap.application AND rc.cfg_name = 'all_permission' AND rc.cfg_val1 = re.application GROUP BY re.id,u.user_account) t WHERE user_account = #{userAccount} AND application = #{application}

SELECT re.*, r.id as role_id from com_resource re, com_res_role rr, com_role r where r.id in
#{item}
AND application = #{application} AND re.active =1 AND r.active =1 AND re.id = rr.res_id AND rr.role_id = r.id GROUP BY re.id, r.id

SELECT r.id, rc.cfg_val1 as application FROM com_role r , com_role_config rc WHERE r.id in
#{item}
AND rc.role_id = r.id AND r.active = 1 AND rc.cfg_name = 'all_permission' GROUP BY r.id , rc.cfg_val1
