mapper.update:
update vms_bt_client_orders
set order_status=#{status}
where client_order_id=#{orderId} and order_channel_id=#{orderChannelId}

mapper.select:
select client_order_id clientOrderId, seller_sku sellerSku, min(order_item_id) orderItemId, sum(quantity) quantity, min(unit_price) unitPrice from vms_bt_client_order_details where order_channel_id=#{orderChannelId} and client_order_id in
${columnName}
group by client_order_id,seller_sku
