define([
  'cms'
], function (cms) {

  cms.controller('popDictCustomCtl', function ($scope, $dictionaryService, $modalInstance, $translate, notify, customValue) {

    $scope.vm = {
      masterData: {},
      valueTypes: {
        custom: 'CUSTOM'
      },
      // 选中的自定义字典对象
      customInfo: { params: [] }
    };

    $scope.initialize = initialize;
    $scope.save = save;
    $scope.cancel = cancel;
    $scope.editDictionary = editDictionary;
    $scope.resetValue = resetValue;

    function initialize() {
      $dictionaryService.getCustoms().then(function (res) {
        $scope.vm.masterData = res.data;

        if (!_.isUndefined(customValue)) {
          var customInfo = customValue.value;
          // 判断当前custom属于哪个custom
          _.forEach($scope.vm.masterData.customs, function (custom) {
            if (_.isEqual(custom.wordName, customInfo.moduleName)) {
              $scope.vm.customInfo = custom;
              // 为当前匹配的custom设置值
              _.forEach($scope.vm.customInfo.params, function (param) {
                param.value = customInfo.userParam[param.paramName] != null ? customInfo.userParam[param.paramName].ruleWordList : null;
              })
            }
          })
        }
      });
    }

    /**
     * 保存现有数据到主页面
     */
    function save () {
      var data = {
        type: $scope.vm.valueTypes.custom,
        value: {
          moduleName: $scope.vm.customInfo.wordName,
          userParam: _getUserParam()
        }
      };
      $modalInstance.close(data);
      //notify.success ($translate.instant('TXT_MSG_UPDATE_SUCCESS'));
      $scope.$close();
    }

    /**
     * 取消新添加数据
     */
    function cancel () {
      $scope.vm.customInfo = { params: [] };

      $scope.$dismiss();
    }

    /**
     * 修改一个原有的字典项
     * @param info
     * @param index
     */
    function editDictionary (info, index) {
      if(!_.isUndefined(info)) {
        var Object = [info];
        $scope.vm.customInfo.params[index].value = Object;
      }
    }

    /**
     * 清空上次被设置的值
     */
    function resetValue () {

      // 初始化每个自定义字典的项目
      _.forEach($scope.vm.customInfo.params, function (param) {
        param.value = null;
      })
    }

    /**
     * 返回userparam对象
     * @returns {{}}
     * @private
     */
    function _getUserParam () {
      var tempUserParam = {};

      _.forEach($scope.vm.customInfo.params, function (param) {
        tempUserParam[param.paramName] = param.value != null ? {ruleWordList: param.value} : null;
      });

      return tempUserParam;
    }
  });
});
