    private StringBuffer schema(Class userCla){
        SolrField solrFieldClass = AnnotationUtils.findAnnotation(userCla, SolrField.class);
        String fieldType = "";
        Boolean required = false;
        String type = "";
        Boolean isIndex = true;
        Boolean multiValued=false;
        Field[] fs = userCla.getDeclaredFields();
        StringBuffer stringBuffer = new StringBuffer();
        for (int i = 0; i < fs.length; i++) {
            Field f = fs[i];
            SolrField solrField = AnnotationUtils.findAnnotation(f, SolrField.class);
            if(solrField != null){
                fieldType = solrField.fieldType();
                required = solrField.required();
                type = solrField.type();
                isIndex = solrField.isIndex();
            }else if(solrFieldClass != null){
                fieldType = solrFieldClass.fieldType();
                required = solrFieldClass.required();
                type = solrFieldClass.type();
                isIndex = solrFieldClass.isIndex();
            }else{
                fieldType = "field";
                required = false;
                type = "";
                isIndex = true;
            }
            if (f.getType().getTypeName().equals(Map.class.getTypeName())) {
                continue;
            }

            if (f.getType().isAssignableFrom(List.class)) {
                multiValued=true;
                Type fc = f.getGenericType();
                if (fc instanceof ParameterizedType) // 【3】如果是泛型参数的类型
                {
                    ParameterizedType pt = (ParameterizedType) fc;

                    Class genericClazz = (Class) pt.getActualTypeArguments()[0];
                    if(StringUtil.isEmpty(type)){
                        type = genericClazz.getTypeName().substring(genericClazz.getTypeName().lastIndexOf(".")+1,genericClazz.getTypeName().length()).trim().toLowerCase();
                    }
                }
            }else{
                multiValued=false;
            }
            String schema = "";
            if(StringUtil.isEmpty(type)){
                type = f.getType().getTypeName().substring(f.getType().getTypeName().lastIndexOf(".")+1,f.getType().getTypeName().length()).trim().toLowerCase();
            }
            if("Integer".equalsIgnoreCase(type)) type = "int";
            String prefix = !fieldType.equals("dynamicField")?"":"*_";
            schema = String.format("<" + fieldType + " name=\"%s\" type=\"%s\" required=\"%s\" multiValued=\"%s\" indexed=\"%s\" stored=\"true\"/>\n", prefix+f.getName(), type, required.toString(), multiValued.toString(), isIndex.toString());

            stringBuffer.append(schema);

        }
        return stringBuffer;
    }
