mapper.select:
SELECT vf_bc_track_detail.year_calc AS 'year_calc', vf_bc_track_detail.month_calc AS 'month_calc', vf_bc_track_detail.order_channel_name AS 'order_channel_name', vf_bc_track_detail.tracking_no AS 'tracking_no', vf_bc_track_detail.syn_ship_no AS 'syn_ship_no', vf_bc_track_detail.main_waybill_num AS 'main_waybill_num', vf_bc_track_detail.source_order_id AS 'source_order_id', vf_bc_track_detail.client_order_number AS 'client_order_number', vf_bc_track_detail.order_number AS 'order_number', vf_track_orders.actual_shipped_weightLB AS 'actual_shipped_weightLB', IF(ROUND(vf_track_orders.actual_shipped_weightLB) > vf_track_orders.actual_shipped_weightLB,ROUND(vf_track_orders.actual_shipped_weightLB),ROUND(vf_track_orders.actual_shipped_weightLB) + 1) AS 'shipped_weightLB', vf_transpotation_detail.goods_weight_lb AS 'goods_weight_lb', IF(ROUND(vf_transpotation_detail.goods_weight_lb) > vf_transpotation_detail.goods_weight_lb,ROUND(vf_transpotation_detail.goods_weight_lb),ROUND(vf_transpotation_detail.goods_weight_lb) + 1) AS 'express_weight_lb', DATE_FORMAT(vf_bc_track_detail.ship_date,'%Y-%m-%d') AS 'ship_date', vf_tax_detail.tax_actual AS 'tax_actual', vf_transpotation_detail.transpotation_amount AS 'transpotation_amount', vf_transpotation_detail.mail_fee AS 'mail_fee', (vf_bs_incidentals_detail.ground_handling_fee + 0.24)/vf_bs_incidentals_detail.tracking_qty AS 'ground_handling_fee', vf_bs_incidentals_detail.storage_charges/vf_bs_incidentals_detail.tracking_qty AS 'storage_charges', IFNULL(idcard.price, 0) AS 'identification_fee' FROM bi_master.vf_bc_track_detail AS vf_bc_track_detail LEFT JOIN bi_master.vf_track_orders AS vf_track_orders ON vf_bc_track_detail.tracking_no = vf_track_orders.tracking_no LEFT JOIN bi_master.vf_tax_detail AS vf_tax_detail ON vf_bc_track_detail.tracking_no = vf_tax_detail.tracking_no LEFT JOIN bi_master.vf_transpotation_detail AS vf_transpotation_detail ON vf_bc_track_detail.tracking_no = vf_transpotation_detail.tracking_no LEFT JOIN bi_master.vf_bs_incidentals_detail AS vf_bs_incidentals_detail ON vf_bc_track_detail.main_waybill_num = vf_bs_incidentals_detail.main_waybill_num LEFT JOIN bi_master.vf_idcard AS idcard ON vf_bc_track_detail.source_order_id = idcard.source_order_id
ORDER BY vf_bc_track_detail.year_calc, vf_bc_track_detail.month_calc, vf_bc_track_detail.order_channel_name
LIMIT #{init_index} , #{get_size}
