mapper.select:
SELECT id, order_channel_id, cart_id, name, `value`, comment, creater, created, modifier, modified FROM voyageone_cms2.cms_mt_dict WHERE order_channel_id = #{order_channel_id}
AND name like CONCAT('%',#{name},'%' )
LIMIT #{offset}, #{rows};

mapper.update:
UPDATE voyageone_cms2.cms_mt_dict
SET name = #{name}, value = #{value}, modified = NOW(), modifier = #{modifier}
WHERE id = #{id} AND modified = #{modified}
